import React, { useEffect, useState } from 'react'
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import Badge from '@mui/material/Badge';
import { NavLink } from 'react-router-dom';
import CloseIcon from '@mui/icons-material/Close';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import { useDispatch, useSelector } from 'react-redux';
import { Table } from '@mui/material';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import { DLT } from '../Redux/actions/action';
import { red } from '@mui/material/colors';
import { flexbox } from '@mui/system';

const Fav = () => {



  const [price, setprice] = useState();

  const getdata = useSelector((state) => state.cartreducer.carts)
  console.log(getdata)

  const dispatch = useDispatch();

  const dlt = (id) => {
    dispatch(DLT(id))
  }

  // const total = () => {
  //   let price = 0;
  //   getdata.map((ele, k) => {
  //     price = ele.price * ele.qnty + price
  //   })
  //   setprice(price)
  // }

  // useEffect(() => {
  //   {console.log("this is it ")}
  //   total()
  // }, [total])


  const [anchorEl, setAnchorEl] = useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  }
















  return (
    <>


 
      <div className='contianer mt-3' >

        <h2 className='text-center'> The Shopify App</h2>

        {
          getdata.length ?
            <div className='card_details'  style={{ width: "24", padding: 10 ,justifyContent : 'center' , margin : 25}}>
              <Table >
                <thead>
                  <tr>
                    <th>photo</th>
                    <th>Restaurant Name</th>
                  </tr>
                </thead>
                <tbody>
                  {
                    getdata.map((e) => {
                      return (
                        <>
                          <tr>
                            <td>
                              <NavLink to={`/cart/${e.id}`} onClick={handleClose}>
                                <img src={e.imgdata} alt="yo" style={{ width: '5rem', height: "5rem" }} />

                              </NavLink>
                            </td>
                            <td>
                              <p>{e.rname}</p>
                              <p>price :₹{e.price}</p>
                              <p>Quantity :{e.qnty}</p>
                              <p >
                                <DeleteForeverIcon onClick={() => dlt(e.id)} style={{ color: 'red', fontSize: 25, cursor: "pointer" }} />
                              </p>
                            </td>

                          </tr>
                        </>
                      )
                    })
                  }
                  <p className='text-center'>Total : ₹ {price}</p>

                </tbody>

              </Table>

            </div> :
            <div className='card_details d-flex justify-content-centre align-items-center' style={{ width: "24rem", padding: 10, position: 'relative' }}>

              <p style={{ fontsize: 22 }}>your cart is empty</p><br />
              <ShoppingCartIcon style={{ padding: 1, width: "5rem", fontSize: '35' }} />
            </div>
        }



      </div>











    </>



  )
}

export default Fav
