const INIt_STATE = {

    carts: []
}


export const cartreducer = (state = INIt_STATE, action) => {
    switch (action.type) {
        case "ADD_CART":
            return {
                ...state,

                carts: [...state.carts, action.payload]
            }


        default:
            return state
    }
}
