const INIt_STATE = {

    carts: []
}


export const cartreducer = (state = INIt_STATE, action) => {
    switch (action.type) {
        case "ADD_CART":
            return {
                ...state,
                carts: [...state.carts, action.payload]
            }

     
  case "RMV_CART":

  const data =state.carts.filter((el)=>el.id !==action.payload);

  return {
    ...state,
    carts : data
  }


        default:
            return state
    }
}
